const nav=()=>{btnHam=document.querySelector(".btn-ham"),btnHam.onclick=function(){document.querySelector("body").classList.toggle("nav-on"),btnHam.classList.toggle("ham-on")};const e=document.querySelectorAll("#navbarNav li");for(let t of e)t.addEventListener("click",()=>{e[0].classList.remove("active"),e[1].classList.remove("active"),e[2].classList.remove("active"),e[3].classList.remove("active"),t.classList.add("active"),document.querySelector("body").classList.remove("nav-on"),document.querySelector("#navbarNav").classList.remove("show"),btnHam.classList.remove("ham-on")})};window.onload=function(){nav()};